import React, { useRef, useState } from "react";
import styles from "../styles/words.module.css";

const Words = ({ word, translate }) => {
  const [inputWord, setInputWord] = useState('');
  const [inputTranslate, setInputTranslate] = useState('');
  const [wordIndex, setWordIndex] = useState(0);

  const firstInput = useRef(null);
  const secondInput = useRef(null);

  const firstOutput = useRef(null);
  const secondOutput = useRef(null);

  const length = () => {
    console.log();
  };

  const handleChange = (e) => {
    setInputWord(firstInput.current.value)
    length();
    if (word[wordIndex] === inputWord[inputWord.length - 1]){
      setWordIndex(wordIndex + 1)
    } else {
      firstOutput.current.textContent = firstOutput.current.textContent.slice(0, wordIndex) + inputWord[inputWord.length - 1]
      // secondOutput.current.value = secondOutput.current.value.slice(0, 2)
    }
  };

  return (
    <div className={styles.container}>
      <div className={styles.wrapper}>
        <input
          ref={firstInput}
          className={`${styles.inputWord} ${styles.firstInputLength}`}
          onChange={handleChange}
        />
        <div ref={firstOutput} className={`${styles.word} ${styles.white}`}>{word}</div>
      </div>
      <div className={styles.wrapper}>
        <input
          ref={secondInput}
          className={`${styles.inputWord} ${styles.secondInputLength}`}
          onChange={handleChange}
        />
        <div ref={secondOutput} className={`${styles.word} ${styles.lime}`}>{translate}</div>
      </div>
    </div>
  );
};

export default Words;
