import React, { useRef, useState } from "react";
import styles from "../styles/words.module.css";

const Words = ({ word, translate }) => {
  const [inputWord, setInputWord] = useState('');
  const [inputTranslate, setInputTranslate] = useState('');
  const overflowInputWord = ""

  const handleChangeWord = (e) => {
    console.log(inputWord.length, e.target.value.length)
    if (inputWord.length < e.target.value.length)
      setInputWord(inputWord + e.target.value[inputWord.length]);
    else
      setInputWord(inputWord.slice(inputWord-1));

    console.log(e.target.value)
  };

  const handleChangeTranslate = (e) => {
    setInputTranslate(e.target.value);
  };

  const renderWord = ({ string, value }) => {
    return [...string].map((char, index) => {
      const isMatched = value[index] === char;
      const color = isMatched ? '#dcdee3' : value[index] ? '#df4f4b' : '#54555e';
      return (
        <span key={index} style={{ color }}>
          {char}
        </span>
      );
    });
  };

  return (
    <div className={styles.container}>
      <div className={styles.wrapper}>
        <input
          className={`${styles.inputWord} ${styles.firstInputLength}`}
          onChange={handleChangeWord}
        />
        <div className={`${styles.word}`}>{ renderWord({string: word, value: inputWord }) }</div>
      </div>
      <div className={styles.wrapper}>
        <input
          className={`${styles.inputWord} ${styles.secondInputLength}`}
          onChange={handleChangeTranslate}
        />
        <div className={`${styles.word}`}>{ renderWord({string: translate, value: inputTranslate}) }</div>
      </div>
    </div>
  );
};

export default Words;
