import React, { useState, useEffect } from 'react';
import styles from '../styles/AdminPanel.module.css';

function AdminPanel() {
  const [users, setUsers] = useState([]);
  const [admins, setAdmins] = useState([]);
  const [words, setWords] = useState([]);

  const [newUser, setNewUser] = useState({ username: '' });
  const [newAdmin, setNewAdmin] = useState({ username: '' });
  const [newWord, setNewWord] = useState({ word: '' });

  const [activeTab, setActiveTab] = useState('users');

  refreshList = () => {
    useEffect(() => {
      fetch('http://localhost:8000/api/data/') // Change the URL if needed
          .then(response => {
              if (!response.ok) {
                  throw new Error('Network response was not ok');
              }
              return response.json();
          })
          .then(data => {
              setData(data);
              setLoading(false);
          })
          .catch(error => {
              setError(error);
              setLoading(false);
          });
  }, []);
  };

  useEffect(() => {
    // Загрузка пользователей, админов и слов из API (здесь можно заменить на реальные запросы)
    setUsers([{ id: 1, username: 'user1' }, { id: 2, username: 'user2' }]);
    setAdmins([{ id: 1, username: 'admin1' }, { id: 2, username: 'admin2' }]);
    setWords([{ id: 1, word: 'word1' }, { id: 2, word: 'word2' }]);
  }, []);

  const handleSubmit = async (event, type) => {
    event.preventDefault();
    let newData;
    if (type === 'user') {
      try {
        const response = await fetch('/users/', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(newUser)
        });
        if (!response.ok) throw new Error('Network response was not ok');
        const user = await response.json();
        setUsers([...users, user]);
        setNewUser({ username: '' });
      } catch (error) {
        console.error('There has been a problem with your fetch operation:', error);
      }
    } else if (type === 'admin') {
      try {
        const response = await fetch('/admins/', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(newAdmin)
        });
        if (!response.ok) throw new Error('Network response was not ok');
        const admin = await response.json();
        setAdmins([...admins, admin]);
        setNewAdmin({ username: '' });
      } catch (error) {
        console.error('There has been a problem with your fetch operation:', error);
      }
    } else if (type === 'word') {
      try {
        const response = await fetch('/words/', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(newWord)
        });
        if (!response.ok) throw new Error('Network response was not ok');
        const word = await response.json();
        setWords([...words, word]);
        setNewWord({ word: '' });
      } catch (error) {
        console.error('There has been a problem with your fetch operation:', error);
      }
    }
  };
  

  return (
    <div className={styles.container}>
      <h2>Admin Panel</h2>
      <div className={styles.tabs}>
        <button className={`${styles.tab} ${activeTab === 'users' ? styles.active : ''}`} onClick={() => setActiveTab('users')}>Users</button>
        <button className={`${styles.tab} ${activeTab === 'admins' ? styles.active : ''}`} onClick={() => setActiveTab('admins')}>Admins</button>
        <button className={`${styles.tab} ${activeTab === 'words' ? styles.active : ''}`} onClick={() => setActiveTab('words')}>Words</button>
      </div>

      {activeTab === 'users' && (
        <>
          <form onSubmit={(e) => handleSubmit(e, 'user')} className={styles.form}>
            <input
              type="text"
              name="username"
              placeholder="Username"
              value={newUser.username}
              onChange={(e) => setNewUser({ username: e.target.value })}
              className={styles.input}
            />
            <button type="submit" className={styles.button}>Add User</button>
          </form>
          <table className={styles.table}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Username</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.username}</td>
                  <td>
                    <button className={styles.actionButton}>Edit</button>
                    <button className={styles.actionButton}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {activeTab === 'admins' && (
        <>
          <form onSubmit={(e) => handleSubmit(e, 'admin')} className={styles.form}>
            <input
              type="text"
              name="username"
              placeholder="Username"
              value={newAdmin.username}
              onChange={(e) => setNewAdmin({ username: e.target.value })}
              className={styles.input}
            />
            <button type="submit" className={styles.button}>Add Admin</button>
          </form>
          <table className={styles.table}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Username</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {admins.map(admin => (
                <tr key={admin.id}>
                  <td>{admin.id}</td>
                  <td>{admin.username}</td>
                  <td>
                    <button className={styles.actionButton}>Edit</button>
                    <button className={styles.actionButton}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {activeTab === 'words' && (
        <>
          <form onSubmit={(e) => handleSubmit(e, 'word')} className={styles.form}>
            <input
              type="text"
              name="word"
              placeholder="Word"
              value={newWord.word}
              onChange={(e) => setNewWord({ word: e.target.value })}
              className={styles.input}
            />
            <button type="submit" className={styles.button}>Add Word</button>
          </form>
          <table className={styles.table}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Word</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {words.map(word => (
                <tr key={word.id}>
                  <td>{word.id}</td>
                  <td>{word.word}</td>
                  <td>
                    <button className={styles.actionButton}>Edit</button>
                    <button className={styles.actionButton}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default AdminPanel;
