import React, { useRef, useState } from "react";
import styles from "../styles/words.module.css";

const Words = ({ word, translate }) => {
  const [inputWord, setInputWord] = useState('');
  const [inputTranslate, setInputTranslate] = useState('');

  const firstInput = useRef(null);
  const secondInput = useRef(null);

  const handleChangeWord = (e) => {
    setInputWord(e.target.value);
  };

  const handleChangeTranslate = (e) => {
    setInputTranslate(e.target.value);
  };

  const renderText = () => {
    return [...word].map((char, index) => {
      const isMatched = inputValue[index] === char;
      const color = isMatched ? 'green' : inputValue[index] ? 'red' : 'black';
      return (
        <span key={index} style={{ color }}>
          {char}
        </span>
      );
    });
  };

  return (
    <div className={styles.container}>
      <div className={styles.wrapper}>
        <input
          ref={firstInput}
          className={`${styles.inputWord} ${styles.firstInputLength}`}
          onChange={handleChange}
        />
        <div ref={firstOutput} className={`${styles.word} ${styles.white}`}>{word}</div>
      </div>
      <div className={styles.wrapper}>
        <input
          ref={secondInput}
          className={`${styles.inputWord} ${styles.secondInputLength}`}
          onChange={handleChange}
        />
        <div ref={secondOutput} className={`${styles.word} ${styles.lime}`}>{translate}</div>
      </div>
    </div>
  );
};

export default Words;
