import React from "react";
import styles from "../styles/words.module.css";

const Words = ({ word, translate }) => {
  const inputWord = document.getElementById("word-input");
  inputWord.setAttribute("size", inputWord.getAttribute("placeholder").length);

  return (
    <div className={styles.container}>
      <div>
        <label for="user-input" className={styles.text}>
          {word}
        </label>
        <input type="text" id="word-input" placeholder="something" autofocus />
      </div>
      <div>
        <label for="translate-input" className={styles.text}>
          {word}
        </label>
        <input
          type="text"
          id="translate-input"
          placeholder="something"
        />
      </div>
    </div>
  );
};

export default Words;
