// src/components/AdminPanel.js
import React, { useState, useEffect } from 'react';
import styles from './AdminPanel.module.css';

function AdminPanel() {
  const [activeTab, setActiveTab] = useState('users');
  const [users, setUsers] = useState([]);
  const [admins, setAdmins] = useState([]);
  const [words, setWords] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8000/api/users/')
      .then(response => response.json())
      .then(data => setUsers(data));

    fetch('http://localhost:8000/api/admins/')
      .then(response => response.json())
      .then(data => setAdmins(data));

    fetch('http://localhost:8000/api/words/')
      .then(response => response.json())
      .then(data => setWords(data));
  }, []);

  const handleInputChange = (e, setter) => {
    const { name, value } = e.target;
    setter(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e, url, state, setState) => {
    e.preventDefault();
    await fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(state)
    }).then(response => response.json())
      .then(data => setState(prevState => [...prevState, data]))
      .catch(error => console.error('Error adding item:', error));
  };

  return (
    <div className={styles.container}>
      <h1>Admin Panel</h1>
      <div className={styles.tabs}>
        <button
          className={`${styles.tab} ${activeTab === 'users' ? styles.active : ''}`}
          onClick={() => setActiveTab('users')}
        >
          Users
        </button>
        <button
          className={`${styles.tab} ${activeTab === 'admins' ? styles.active : ''}`}
          onClick={() => setActiveTab('admins')}
        >
          Admins
        </button>
        <button
          className={`${styles.tab} ${activeTab === 'words' ? styles.active : ''}`}
          onClick={() => setActiveTab('words')}
        >
          Words
        </button>
      </div>

      {activeTab === 'users' && (
        <>
          <form onSubmit={e => handleSubmit(e, 'http://localhost:8000/api/users/', newUser, setUsers)} className={styles.form}>
            {/* Input fields for user */}
            <input
              type="text"
              name="username"
              placeholder="Username"
              onChange={e => handleInputChange(e, setNewUser)}
              className={styles.input}
            />
            <button type="submit" className={styles.button}>Add User</button>
          </form>
          <table className={styles.table}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Username</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.username}</td>
                  <td>
                    <button className={styles.actionButton}>Edit</button>
                    <button className={styles.actionButton}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {activeTab === 'admins' && (
        <>
          <form onSubmit={e => handleSubmit(e, 'http://localhost:8000/api/admins/', newAdmin, setAdmins)} className={styles.form}>
            {/* Input fields for admin */}
            <input
              type="text"
              name="username"
              placeholder="Username"
              onChange={e => handleInputChange(e, setNewAdmin)}
              className={styles.input}
            />
            <button type="submit" className={styles.button}>Add Admin</button>
          </form>
          <table className={styles.table}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Username</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {admins.map(admin => (
                <tr key={admin.id}>
                  <td>{admin.id}</td>
                  <td>{admin.username}</td>
                  <td>
                    <button className={styles.actionButton}>Edit</button>
                    <button className={styles.actionButton}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {activeTab === 'words' && (
        <>
          <form onSubmit={e => handleSubmit(e, 'http://localhost:8000/api/words/', newWord, setWords)} className={styles.form}>
            {/* Input fields for word */}
            <input
              type="text"
              name="word"
              placeholder="Word"
              onChange={e => handleInputChange(e, setNewWord)}
              className={styles.input}
            />
            <button type="submit" className={styles.button}>Add Word</button>
          </form>
          <table className={styles.table}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Word</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {words.map(word => (
                <tr key={word.id}>
                  <td>{word.id}</td>
                  <td>{word.word}</td>
                  <td>
                    <button className={styles.actionButton}>Edit</button>
                    <button className={styles.actionButton}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default AdminPanel;
