// src/components/admin/WordsAdmin.js
import React, { useState, useEffect } from 'react';
import styles from '../../styles/admin.module.css';
import WordForm from './WordForm';

const WordsAdmin = () => {
  const [words, setWords] = useState([]);
  const [editingWord, setEditingWord] = useState(null);

  useEffect(() => {
    fetch('/api/words')
      .then(response => response.json())
      .then(data => setWords(data))
      .catch(error => console.error('Error fetching words:', error));
  }, []);

  const handleAddWord = (word) => {
    setWords([...words, word]);
  };

  const handleUpdateWord = (updatedWord) => {
    setWords(words.map(word => (word.id === updatedWord.id ? updatedWord : word)));
    setEditingWord(null);
  };

  const handleDeleteWord = (wordId) => {
    setWords(words.filter(word => word.id !== wordId));
  };

  return (
    <div className={`${styles.adminContainer} container`}>
      <h2>Manage Words</h2>
      <WordForm
        onAdd={handleAddWord}
        onUpdate={handleUpdateWord}
        editingWord={editingWord}
      />
      <table className={styles.table}>
        <thead>
          <tr>
            <th className={styles.th}>ID</th>
            <th className={styles.th}>Word</th>
            <th className={styles.th}>Description</th>
            <th className={styles.th}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {words.map(word => (
            <tr key={word.id}>
              <td className={styles.td}>{word.id}</td>
              <td className={styles.td}>{word.word}</td>
              <td className={styles.td}>{word.description}</td>
              <td className={styles.td}>
                <button
                  onClick={() => setEditingWord(word)}
                  className={styles.button}
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDeleteWord(word.id)}
                  className={styles.button}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default WordsAdmin;
