// src/components/admin/WordForm.js
import React, { useState, useEffect } from 'react';
import styles from '../../styles/admin.module.css';

const WordForm = ({ onAdd, onUpdate, editingWord }) => {
  const [word, setWord] = useState('');
  const [description, setDescription] = useState('');

  useEffect(() => {
    if (editingWord) {
      setWord(editingWord.word);
      setDescription(editingWord.description);
    }
  }, [editingWord]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingWord) {
      onUpdate({ ...editingWord, word, description });
    } else {
      onAdd({ id: Date.now(), word, description });
    }
    setWord('');
    setDescription('');
  };

  return (
    <form className={styles.form} onSubmit={handleSubmit}>
      <div>
        <label htmlFor="word" className={styles.label}>Word:</label>
        <input
          type="text"
          id="word"
          value={word}
          onChange={(e) => setWord(e.target.value)}
          className={`${styles.inputWord} ${styles.firstInputLength}`}
          required
        />
      </div>
      <div>
        <label htmlFor="description" className={styles.label}>Description:</label>
        <input
          type="text"
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className={styles.inputWord}
          required
        />
      </div>
      <button type="submit" className={styles.button}>
        {editingWord ? 'Update Word' : 'Add Word'}
      </button>
    </form>
  );
};

export default WordForm;
