import React, { useState, useEffect } from "react";
import styles from "../styles/AdminPanel.module.css";
import api from "../api";
import axios from "axios";

function AdminPanel() {
  const [users, setUsers] = useState([]);
  const [admins, setAdmins] = useState([]);
  const [words, setWords] = useState([]);

  const [newUser, setNewUser] = useState({
    username: "",
    email: "",
    password_hash: "",
    english_level: "",
  });
  const [newAdmin, setNewAdmin] = useState({ username: "" });
  const [newWord, setNewWord] = useState({ word: "" });

  const [activeTab, setActiveTab] = useState("users");

  const fetchUsersData = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/list/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error occurred:", error);
    }
  };

  useEffect(() => {
    fetchUsersData();
  }, []);

  const handleSubmit = async (event, type) => {
    event.preventDefault();
    let newData;
    if (type === "user") {
      try {
        const response = await axios.post(
          "http://localhost:8000/api/create/users",
          newUser
        );
        setNewUser({
          username: "",
          email: "",
          password: "",
          english_level: "",
        });
      } catch (error) {
        console.error(
          "There has been a problem with create user operation:",
          error
        );
      }
    } else if (type === "admin") {
    } else if (type === "word") {
    }

    fetchUsersData();
  };

  const handleDelete = async (event, type) => {
    event.preventDefault();
    let newData;
    if (type === "user") {
      try {
        const userId = event.target.value;
        const url = `http://localhost:8000/api/delete/users/${userId}/`;

        console.log(`Deleting user at URL: ${url}`);

        const response = await axios.delete(url);

        console.log("User deleted successfully:", response.data);
      } catch (error) {
        console.error(
          "There has been a problem with the delete user operation:",
          error.response ? error.response.data : error.message
        );
      }
    } else if (type === "admin") {
    } else if (type === "word") {
    }

    fetchUsersData();
  };

  const handleEditUser = async (event, user) => {
    event.preventDefault();
      try {
        const url = `http://localhost:8000/api/update/users/${user.id_user}/`;

        const updatedData = {
          username: user.username,
          email: user.email,
          english_level: user.english_level,
          is_email_verificated: user.is_email_verificated,
          account_created_at: user.account_created_at,
          password_changed_at: user.password_changed_at,
          last_day_online: user.last_day_online,
          days_in_berserk: user.days_in_berserk,
        };

        console.log(`Updating user at URL: ${url}`, updatedData);

        const response = await axios.put(url, updatedData);

        console.log("User updated successfully:", response.data);
      } catch (error) {
        console.error(
          "There has been a problem with the update user operation:",
          error.response ? error.response.data : error.message
        );
      }
    fetchUsersData();
  };

  return (
    <div className={styles.admin_container}>
      <h2 className={styles.admin_title}>Панель администратора</h2>
      <div className={styles.admin_tabs}>
        <button
          className={`${styles.admin_tab} ${
            activeTab === "users" ? styles.active : ""
          }`}
          onClick={() => setActiveTab("users")}
        >
          Пользователи
        </button>
        <button
          className={`${styles.admin_tab} ${
            activeTab === "admins" ? styles.active : ""
          }`}
          onClick={() => setActiveTab("admins")}
        >
          Администраторы
        </button>
        <button
          className={`${styles.admin_tab} ${
            activeTab === "words" ? styles.active : ""
          }`}
          onClick={() => setActiveTab("words")}
        >
          Слова
        </button>
      </div>

      {activeTab === "users" && (
        <>
          <form
            onSubmit={(e) => handleSubmit(e, "user")}
            className={styles.admin_form}
          >
            <input
              type="text"
              name="username"
              placeholder="Пользователь"
              value={newUser.username}
              onChange={(e) =>
                setNewUser({ ...newUser, username: e.target.value })
              }
              className={styles.admin_input}
            />
            <input
              type="text"
              name="email"
              placeholder="Email"
              value={newUser.email}
              onChange={(e) =>
                setNewUser({ ...newUser, email: e.target.value })
              }
              className={styles.admin_input}
            />
            <input
              type="text"
              name="password"
              placeholder="Пароль"
              value={newUser.password_hash}
              onChange={(e) =>
                setNewUser({ ...newUser, password_hash: e.target.value })
              }
              className={styles.admin_input}
            />
            <input
              type="text"
              name="english level"
              placeholder="Уровень английского"
              value={newUser.english_level}
              onChange={(e) =>
                setNewUser({ ...newUser, english_level: e.target.value })
              }
              className={styles.admin_input}
            />
            <button type="submit" className={styles.admin_button}>
              Add User
            </button>
          </form>
          <table className={styles.admin_table}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Usermane</th>
                <th>Email</th>
                <th>Уровень английского</th>
                <th>Почта подтверждена</th>
                <th>Дата создания аккаунта</th>
                <th>Дата изменения пароля</th>
                <th>Последний вход в аккаунт</th>
                <th>Количество дней в берверке</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user.id_user}>
                  <td>
                    <div contentEditable="true">{user.id_user}</div>
                  </td>
                  <td>
                    <div contentEditable="true">{user.username}</div>
                  </td>
                  <td>
                    <div contentEditable="true">{user.email}</div>
                  </td>
                  <td>
                    <div contentEditable="true">{user.english_level}</div>
                  </td>
                  <td>
                    <div contentEditable="true">
                      {String(user.is_email_verificated)}
                    </div>
                  </td>
                  <td>
                    <div contentEditable="true">{user.account_created_at}</div>
                  </td>
                  <td>
                    <div contentEditable="true">{user.password_changed_at}</div>
                  </td>
                  <td>
                    <div contentEditable="true">{user.last_day_online}</div>
                  </td>
                  <td>
                    <div contentEditable="true">{user.days_in_berserk}</div>
                  </td>
                  <td className={styles.border_none}>
                    <button
                      className={styles.admin_actionButton}
                      value={user.id_user}
                      onClick={(e) => handleEditUser(e, user)}
                    >
                      Изменить
                    </button>
                    <button
                      className={styles.admin_actionButton}
                      value={user.id_user}
                      onClick={(e) => handleDelete(e, "user")}
                    >
                      Удалить
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {activeTab === "admins" && (
        <>
          <form
            onSubmit={(e) => handleSubmit(e, "admin")}
            className={styles.admin_form}
          >
            <input
              type="text"
              name="username"
              placeholder="Username"
              value={newAdmin.username}
              onChange={(e) => setNewAdmin({ username: e.target.value })}
              className={styles.admin_input}
            />
            <button type="submit" className={styles.admin_button}>
              Add Admin
            </button>
          </form>
          <table className={styles.admin_table}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Username</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {admins.map((admin) => (
                <tr key={admin.id}>
                  <td>{admin.id}</td>
                  <td>{admin.username}</td>
                  <td>
                    <button className={styles.admin_actionButton}>Edit</button>
                    <button className={styles.admin_actionButton}>
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {activeTab === "words" && (
        <>
          <form
            onSubmit={(e) => handleSubmit(e, "word")}
            className={styles.admin_form}
          >
            <input
              type="text"
              name="word"
              placeholder="Word"
              value={newWord.word}
              onChange={(e) => setNewWord({ word: e.target.value })}
              className={styles.admin_input}
            />
            <button type="submit" className={styles.admin_button}>
              Add Word
            </button>
          </form>
          <table className={styles.admin_table}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Word</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {words.map((word) => (
                <tr key={word.id}>
                  <td>{word.id}</td>
                  <td>{word.word}</td>
                  <td>
                    <button className={styles.admin_actionButton}>Edit</button>
                    <button className={styles.admin_actionButton}>
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default AdminPanel;
