import React, { useState, useEffect } from 'react';
import styles from '../styles/AdminPanel.module.css';

function AdminPanel() {
  const [users, setUsers] = useState([]);
  const [admins, setAdmins] = useState([]);
  const [words, setWords] = useState([]);

  const [newUser, setNewUser] = useState({ username: '' });
  const [newAdmin, setNewAdmin] = useState({ username: '' });
  const [newWord, setNewWord] = useState({ word: '' });

  const [activeTab, setActiveTab] = useState('users');

  useEffect(() => {
    // Загрузка пользователей, админов и слов из API (здесь можно заменить на реальные запросы)
    setUsers([{ id: 1, username: 'user1' }, { id: 2, username: 'user2' }]);
    setAdmins([{ id: 1, username: 'admin1' }, { id: 2, username: 'admin2' }]);
    setWords([{ id: 1, word: 'word1' }, { id: 2, word: 'word2' }]);
  }, []);

  const handleSubmit = async (event, type) => {
    event.preventDefault();
    let newData;
    if (type === 'user') {
      try {
        const response = await fetch('/users/', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(newUser)
        });
        if (!response.ok) throw new Error('Network response was not ok');
        const user = await response.json();
        setUsers([...users, user]);
        setNewUser({ username: '' });
      } catch (error) {
        console.error('There has been a problem with your fetch operation:', error);
      }
    } else if (type === 'admin') {
      try {
        const response = await fetch('/admins/', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(newAdmin)
        });
        if (!response.ok) throw new Error('Network response was not ok');
        const admin = await response.json();
        setAdmins([...admins, admin]);
        setNewAdmin({ username: '' });
      } catch (error) {
        console.error('There has been a problem with your fetch operation:', error);
      }
    } else if (type === 'word') {
      try {
        const response = await fetch('/words/', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(newWord)
        });
        if (!response.ok) throw new Error('Network response was not ok');
        const word = await response.json();
        setWords([...words, word]);
        setNewWord({ word: '' });
      } catch (error) {
        console.error('There has been a problem with your fetch operation:', error);
      }
    }
  };

  const handleEdit = async (type, id, updatedData) => {
    if (type === 'user') {
      try {
        const response = await fetch(`/users/${id}/`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(updatedData)
        });
        if (!response.ok) throw new Error('Network response was not ok');
        const user = await response.json();
        setUsers(users.map(user => (user.id === id ? updatedData : user)));
      } catch (error) {
        console.error('There has been a problem with your fetch operation:', error);
      }
    } else if (type === 'admin') {
      try {
        const response = await fetch(`/admins/${id}/`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(updatedData)
        });
        if (!response.ok) throw new Error('Network response was not ok');
        const admin = await response.json();
        setAdmins(admins.map(admin => (admin.id === id ? updatedData : admin)));
      } catch (error) {
        console.error('There has been a problem with your fetch operation:', error);
      }
    } else if (type === 'word') {
      try {
        const response = await fetch(`/words/${id}/`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(updatedData)
        });
        if (!response.ok) throw new Error('Network response was not ok');
        const word = await response.json();
        setWords(words.map(word => (word.id === id ? updatedData : word)));
      } catch (error) {
        console.error('There has been a problem with your fetch operation:', error);
      }
    }
  };

  const handleDelete = async (type, id) => {
    if (type === 'user') {
      try {
        const response = await fetch(`/users/${id}/`, {
          method: 'DELETE'
        });
        if (!response.ok) throw new Error('Network response was not ok');
        setUsers(users.filter(user => user.id !== id));
      } catch (error) {
        console.error('There has been a problem with your fetch operation:', error);
      }
    } else if (type === 'admin') {
      try {
        const response = await fetch(`/admins/${id}/`, {
          method: 'DELETE'
        });
        if (!response.ok) throw new Error('Network response was not ok');
        setAdmins(admins.filter(admin => admin.id !== id));
      } catch (error) {
        console.error('There has been a problem with your fetch operation:', error);
      }
    } else if (type === 'word') {
      try {
        const response = await fetch(`/words/${id}/`, {
          method: 'DELETE'
        });
        if (!response.ok) throw new Error('Network response was not ok');
        setWords(words.filter(word => word.id !== id));
      } catch (error) {
        console.error('There has been a problem with your fetch operation:', error);
      }
    }
  };

  return (
    <div className={styles.adminPanel}>
      <button onClick={() => setActiveTab('users')}>Users</button>
      <button onClick={() => setActiveTab('admins')}>Admins</button>
      <button onClick={() => setActiveTab('words')}>Words</button>

      {activeTab === 'users' && (
        <div className={styles.tabContent}>
          <form onSubmit={(e) => handleSubmit(e, 'user')}>
            <input
              type="text"
              value={newUser.username}
              onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
              placeholder="Username"
            />
            <button type="submit">Add User</button>
          </form>
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Username</th>
                <th>Email</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.username}</td>
                  <td>{user.email}</td>
                  <td>
                    <button onClick={() => handleEdit('user', user.id, { username: prompt('New Username:', user.username) })}>Edit</button>
                    <button onClick={() => handleDelete('user', user.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {activeTab === 'admins' && (
        <div className={styles.tabContent}>
          <form onSubmit={(e) => handleSubmit(e, 'admin')}>
            <input
              type="text"
              value={newAdmin.username}
              onChange={(e) => setNewAdmin({ ...newAdmin, username: e.target.value })}
              placeholder="Username"
            />
            <button type="submit">Add Admin</button>
          </form>
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Username</th>
                <th>Email</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {admins.map(admin => (
                <tr key={admin.id}>
                  <td>{admin.id}</td>
                  <td>{admin.username}</td>
                  <td>{admin.email}</td>
                  <td>
                    <button onClick={() => handleEdit('admin', admin.id, { username: prompt('New Username:', admin.username) })}>Edit</button>
                    <button onClick={() => handleDelete('admin', admin.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {activeTab === 'words' && (
        <div className={styles.tabContent}>
          <form onSubmit={(e) => handleSubmit(e, 'word')}>
            <input
              type="text"
              value={newWord.word}
              onChange={(e) => setNewWord({ ...newWord, word: e.target.value })}
              placeholder="Word"
            />
            <button type="submit">Add Word</button>
          </form>
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Word</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {words.map(word => (
                <tr key={word.id}>
                  <td>{word.id}</td>
                  <td>{word.word}</td>
                  <td>
                    <button onClick={() => handleEdit('word', word.id, { word: prompt('New Word:', word.word) })}>Edit</button>
                    <button onClick={() => handleDelete('word', word.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default AdminPanel;
