// src/components/admin/AdminForm.js
import React, { useState, useEffect } from 'react';
import styles from '../../styles/admin.module.css';

const AdminForm = ({ onAdd, onUpdate, editingAdmin }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');

  useEffect(() => {
    if (editingAdmin) {
      setName(editingAdmin.name);
      setEmail(editingAdmin.email);
    }
  }, [editingAdmin]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingAdmin) {
      onUpdate({ ...editingAdmin, name, email });
    } else {
      onAdd({ id: Date.now(), name, email });
    }
    setName('');
    setEmail('');
  };

  return (
    <form className={styles.form} onSubmit={handleSubmit}>
      <div>
        <label htmlFor="name" className={styles.label}>Name:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className={`${styles.inputWord} ${styles.firstInputLength}`}
          required
        />
      </div>
      <div>
        <label htmlFor="email" className={styles.label}>Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className={styles.inputWord}
          required
        />
      </div>
      <button type="submit" className={styles.button}>
        {editingAdmin ? 'Update Admin' : 'Add Admin'}
      </button>
    </form>
  );
};

export default AdminForm;
