import React, { useRef, useState } from "react";
import styles from "../styles/words.module.css";

const Words = ({ word, translate }) => {
  let [inputWord, setInputWord] = useState('');
  let [inputTranslate, setInputTranslate] = useState('');
  let [wordIndex, setWordIndex] = useState(0);
  const firstInputLength = useRef(null);
  const secondInputLength = useRef(null);
  const length = () => {
    console.log("kek");
  };

  const handleChange = (e) => {
    console.log(firstInputLength.current.value)
    setInputWord(firstInputLength.current.value)
    setInputTranslate(secondInputLength.current.value)
    length();
    if (word[wordIndex] == inputWord[-1]){
      console.log(true, word, inputWord, wordIndex)
      setWordIndex((wordIndex) => wordIndex + 1)
    } else {
      console.log(false, word, inputWord, wordIndex)
    }
  };

  return (
    <div className={styles.container}>
      <div className={styles.wrapper}>
        <input
          ref={firstInputLength}
          className={`${styles.inputWord} ${styles.firstInputLength}`}
          onChange={handleChange}
        />
        <div className={`${styles.word} ${styles.white}`}>{word}</div>
      </div>
      <div className={styles.wrapper}>
        <input
          ref={secondInputLength}
          className={`${styles.inputWord} ${styles.secondInputLength}`}
          onChange={handleChange}
        />
        <div className={`${styles.word} ${styles.lime}`}>{translate}</div>
      </div>
    </div>
  );
};

export default Words;
