import React, { useRef, useState } from "react";
import styles from "../styles/words.module.css";

const Words = ({ word, translate }) => {
  const [inputWord, setInputWord] = useState('');
  const [inputTranslate, setInputTranslate] = useState('');
  const firstInputLength = useRef(null);
  const secondInputLength = useRef(null);
  const length = () => {
    console.log(firstInputLength.current.value);
  };

  const handleChange = () => {
    setInputWord(firstInputLength.current.value)
    length();
  };

  return (
    <div className={styles.container}>
      <div className={styles.wrapper}>
        <input
          ref={firstInputLength}
          className={`${styles.inputWord} ${styles.firstInputLength}`}
          onChange={handleChange}
        />
        <div className={`${styles.word} ${styles.white}`}>{word}</div>
      </div>
      <div className={styles.wrapper}>
        <input
          className={`${styles.inputWord} ${styles.secondInputLength}`}
          onChange={handleChange}
        />
        <div className={`${styles.word} ${styles.lime}`}>{translate}</div>
      </div>
    </div>
  );
};

export default Words;
