import React, { useState, useEffect } from "react";
import styles from "../styles/AdminPanel.module.css";
import api from "../api";
import axios from "axios";

function AdminPanel() {
  const [users, setUsers] = useState([]);
  const [admins, setAdmins] = useState([]);
  const [words, setWords] = useState([]);

  const [newUser, setNewUser] = useState({
    username: "",
    email: "",
    password: "",
    english_level: "",
  });
  const [newAdmin, setNewAdmin] = useState({ username: "" });
  const [newWord, setNewWord] = useState({ word: "" });

  const [activeTab, setActiveTab] = useState("users");

  const fetchUsersData = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/list/users");
      console.log("Data received:", response.data);
      setUsers(response.data);
    } catch (error) {
      console.error("Error occurred:", error);
    }

    useEffect(() => {
      fetchUsersData();
    }, []);

    const handleSubmit = async (event, type) => {
      event.preventDefault();
      let newData;
      if (type === "user") {
        try {
          const response = await axios.post(
            "http://localhost:8000/api/list/users"
          );
          console.log("Data posted:", response.status);
          const user = await response.json();
          setUsers([...users, user]);
          setNewUser({
            username: "",
            email: "",
            password: "",
            english_level: "",
          });
        } catch (error) {
          console.error(
            "There has been a problem with your create user operation:",
            error
          );
        }
      } else if (type === "admin") {
        try {
          const response = await fetch("/admins/", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(newAdmin),
          });
          if (!response.ok) throw new Error("Network response was not ok");
          const admin = await response.json();
          setAdmins([...admins, admin]);
          setNewAdmin({ username: "" });
        } catch (error) {
          console.error(
            "There has been a problem with your fetch operation:",
            error
          );
        }
      } else if (type === "word") {
        try {
          const response = await fetch("/words/", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(newWord),
          });
          if (!response.ok) throw new Error("Network response was not ok");
          const word = await response.json();
          setWords([...words, word]);
          setNewWord({ word: "" });
        } catch (error) {
          console.error(
            "There has been a problem with your fetch operation:",
            error
          );
        }
      }
    };

    return (
      <div className={styles.container}>
        <h2>Admin Panel</h2>
        <div className={styles.tabs}>
          <button
            className={`${styles.tab} ${
              activeTab === "users" ? styles.active : ""
            }`}
            onClick={() => setActiveTab("users")}
          >
            Users
          </button>
          <button
            className={`${styles.tab} ${
              activeTab === "admins" ? styles.active : ""
            }`}
            onClick={() => setActiveTab("admins")}
          >
            Admins
          </button>
          <button
            className={`${styles.tab} ${
              activeTab === "words" ? styles.active : ""
            }`}
            onClick={() => setActiveTab("words")}
          >
            Words
          </button>
        </div>

        {activeTab === "users" && (
          <>
            <form
              onSubmit={(e) => handleSubmit(e, "user")}
              className={styles.form}
            >
              <input
                type="text"
                name="username"
                placeholder="Username"
                value={newUser.username}
                onChange={(e) => setNewUser({ username: e.target.value })}
                className={styles.input}
              />
              <input
                type="text"
                name="email"
                placeholder="email"
                value={newUser.email}
                onChange={(e) => setNewUser({ email: e.target.value })}
                className={styles.input}
              />
              <input
                type="text"
                name="password"
                placeholder="password"
                value={newUser.password}
                onChange={(e) => setNewUser({ password: e.target.value })}
                className={styles.input}
              />
              <input
                type="text"
                name="english level"
                placeholder="english level"
                value={newUser.english_level}
                onChange={(e) => setNewUser({ english_level: e.target.value })}
                className={styles.input}
              />
              <button type="submit" className={styles.button}>
                Add User
              </button>
            </form>
            <table className={styles.table}>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Usermane</th>
                  <th>Email</th>
                  <th>English level</th>
                  <th>Is email verificated</th>
                  <th>Account created date </th>
                  <th>Password changed date</th>
                  <th>Last day online</th>
                  <th>Days in berserk</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user.id_user}>
                    <td>{user.id_user}</td>
                    <td>{user.username}</td>
                    <td>{user.email}</td>
                    <td>{user.english_level}</td>
                    <td>{String(user.is_email_verificated)}</td>
                    <td>{user.account_created_at}</td>
                    <td>{user.password_changed_at}</td>
                    <td>{user.last_day_online}</td>
                    <td>{user.days_in_berserk}</td>
                    <td>
                      <button className={styles.actionButton}>Edit</button>
                      <button className={styles.actionButton}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </>
        )}

        {activeTab === "admins" && (
          <>
            <form
              onSubmit={(e) => handleSubmit(e, "admin")}
              className={styles.form}
            >
              <input
                type="text"
                name="username"
                placeholder="Username"
                value={newAdmin.username}
                onChange={(e) => setNewAdmin({ username: e.target.value })}
                className={styles.input}
              />
              <button type="submit" className={styles.button}>
                Add Admin
              </button>
            </form>
            <table className={styles.table}>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Username</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {admins.map((admin) => (
                  <tr key={admin.id}>
                    <td>{admin.id}</td>
                    <td>{admin.username}</td>
                    <td>
                      <button className={styles.actionButton}>Edit</button>
                      <button className={styles.actionButton}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </>
        )}

        {activeTab === "words" && (
          <>
            <form
              onSubmit={(e) => handleSubmit(e, "word")}
              className={styles.form}
            >
              <input
                type="text"
                name="word"
                placeholder="Word"
                value={newWord.word}
                onChange={(e) => setNewWord({ word: e.target.value })}
                className={styles.input}
              />
              <button type="submit" className={styles.button}>
                Add Word
              </button>
            </form>
            <table className={styles.table}>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Word</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {words.map((word) => (
                  <tr key={word.id}>
                    <td>{word.id}</td>
                    <td>{word.word}</td>
                    <td>
                      <button className={styles.actionButton}>Edit</button>
                      <button className={styles.actionButton}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </>
        )}
      </div>
    );
  };
}

export default AdminPanel;
