import React from "react";
import styles from "../styles/words.module.css"

const Words = ({word, translate}) => {
    const textToType = "Это пример текста для ввода.";
const textElement = document.getElementById("text-to-type");
const userInput = document.getElementById("user-input");
const result = document.getElementById("result");

textElement.textContent = textToType;

userInput.addEventListener("input", () => {
    const inputValue = userInput.value;

    if (inputValue === textToType) {
        result.textContent = "Поздравляем! Вы завершили ввод.";
        userInput.disabled = true; // Отключаем поле ввода
    } else if (textToType.startsWith(inputValue)) {
        result.textContent = ""; // Очистить результат
        userInput.style.borderColor = "green"; // Успешный ввод
    } else {
        userInput.style.borderColor = "red"; // Ошибка в вводе
    }
});



  return (
    <div className={styles.container}>
      <div id="text-to-type" class={styles.text}></div>
      <input type="text" id="user-input" placeholder="Начните печатать..." autofocus />
      <div>{translate}</div>
    </div>
  );
};

export default Words;
